css:
body {
  font-family: "Helvetica", sans-serif;
  margin: 0;
  padding: 30px;
  background-color: #fff;
  color: #333;
}

.print-container {
  border: 2px solid #444;
  border-radius: 8px;
  max-width: 800px;
  margin: auto;
  padding: 30px;
}

h2 {
  text-align: center;
  margin-bottom: 30px;
  color: #2e86de;
}

.field-group {
  margin-bottom: 20px;
}

.field-row {
  display: flex;
  justify-content: space-between;
  margin-bottom: 15px;
  border-bottom: 1px dashed #ccc;
  padding-bottom: 10px;
}

.field-label {
  font-weight: bold;
  width: 35%;
}

.field-value {
  width: 60%;
  text-align: left;
}
